static int findMaxSub(vector<int>& nums, size_t start, size_t end, size_t& maxStart, size_t& maxEnd)
{
	if (start == end)
	{
		maxStart = start;
		maxEnd = end;
		return 0;
	}

	int max = INT_MIN;
	size_t retMaxStart = start;
	size_t retMaxEnd = start + 1;

	size_t curMaxStart = start, curMaxEnd = start + 1;
	int curMax = 0;
	size_t alterMaxStart = curMaxStart, alterMaxEnd = curMaxEnd;
	int alterMax = curMax;
	for (size_t i = start; i < end; ++i)
	{
		if (i > 0 && curMax > max)
		{
			max = curMax;
			retMaxStart = curMaxStart;
			retMaxEnd = curMaxEnd;
		}

		int cur = nums[i];
		curMax += cur;
		curMaxEnd = i + 1;

		alterMax += cur;
		alterMaxEnd = i + 1;
		if (alterMax > curMax)
		{
			curMax = alterMax;
			curMaxStart = alterMaxStart;
			curMaxEnd = alterMaxEnd;
		}
		
		if (cur < 0)
		{
			alterMax = 0;
			alterMaxStart = i + 1;
		}
	}

	if (curMax > max)
	{
		max = curMax;
		retMaxStart = curMaxStart;
		retMaxEnd = curMaxEnd;
	}

	maxStart = retMaxStart;
	maxEnd = retMaxEnd;

	return max;
}

static int findMinSub(vector<int>& nums, size_t start, size_t end, size_t& minStart, size_t& minEnd)
{
	if (start == end)
	{
		minStart = start;
		minEnd = end;
		return 0;
	}

	int min = INT_MAX;
	size_t retMinStart = start;
	size_t retMinEnd = start + 1;

	size_t curMinStart = start, curMinEnd = start + 1;
	int curMin = 0;
	size_t alterMinStart = curMinStart, alterMinEnd = curMinEnd;
	int alterMin = curMin;
	for (size_t i = start; i < end; ++i)
	{
		if (i > 0 && curMin < min)
		{
			min = curMin;
			retMinStart = curMinStart;
			retMinEnd = curMinEnd;
		}

		int cur = nums[i];
		curMin += cur;
		curMinEnd = i + 1;

		alterMin += cur;
		alterMinEnd = i + 1;
		if (alterMin < curMin)
		{
			curMin = alterMin;
			curMinStart = alterMinStart;
			curMinEnd = alterMinEnd;
		}

		if (cur > 0)
		{
			alterMin = 0;
			alterMinStart = i + 1;
		}
	}

	if (curMin < min)
	{
		min = curMin;
		retMinStart = curMinStart;
		retMinEnd = curMinEnd;
	}

	minStart = retMinStart;
	minEnd = retMinEnd;

	return min;
}

int maxDiffSubArrays(vector<int>& nums)
{
	size_t numsLen = nums.size();
	if (numsLen < 2)
		return 0;

	if (numsLen == 2)
		return abs(nums[0] - nums[1]);

	size_t allMinStart = 0;
	size_t allMinEnd = allMinStart;
	int allMin = findMinSub(nums, 0, numsLen, allMinStart, allMinEnd);

	int maxDiff = 0;

	for (size_t i = 1; i < numsLen; ++i)
	{
		size_t leftStart = 0;
		size_t leftEnd = i;
		size_t rightStart = i;
		size_t rightEnd = numsLen;

		size_t leftMaxStart = leftStart;
		size_t leftMaxEnd = leftMaxStart;
		int leftMax = findMaxSub(nums, leftStart, leftEnd, leftMaxStart, leftMaxEnd);

		size_t rightMaxStart = rightStart;
		size_t rightMaxEnd = rightMaxStart;
		int rightMax = findMaxSub(nums, rightStart, rightEnd, rightMaxStart, rightMaxEnd);

		if ((rightMax - allMin) >= maxDiff)
		{
			size_t leftMinStart = leftStart;
			size_t leftMinEnd = leftMinStart;
			int leftMin = findMinSub(nums, leftStart, leftEnd, leftMinStart, leftMinEnd);
			int max2 = abs(rightMax - leftMin);
			if (max2 > maxDiff)
				maxDiff = max2;
		}
		
		if ((leftMax - allMin) >= maxDiff)
		{
			size_t rightMinStart = rightStart;
			size_t rightMinEnd = rightMinStart;
			int rightMin = findMinSub(nums, rightStart, rightEnd, rightMinStart, rightMinEnd);
			int max1 = abs(leftMax - rightMin);
			if (max1 > maxDiff)
				maxDiff = max1;
		}
		
	}

	return maxDiff;
}

struct MaxMin
{
	int max;
	int min;
};

int maxDiffSubArraysFaster(vector<int>& nums)
{
	size_t numsLen = nums.size();
	if (numsLen < 2)
		return 0;

	if (numsLen == 2)
		return abs(nums[0] - nums[1]);

	int maxDiff = 0;

	// Left
	vector<MaxMin> leftPart;
	leftPart.resize(numsLen + 1);
	
	// Max
	int leftMax = INT_MIN;

	int curLeftMax = 0;
	int alterLeftMax = curLeftMax;

	// Min
	int leftMin = INT_MAX;

	int curLeftMin = 0;
	int alterLeftMin = curLeftMin;

	// Right
	vector<MaxMin> rightPart;
	rightPart.resize(numsLen + 1);

	// Max
	int rightMax = INT_MIN;

	int curRightMax = 0;
	int alterRightMax = curRightMax;

	// Min
	int rightMin = INT_MAX;

	int curRightMin = 0;
	int alterRightMin = curRightMin;

	for (size_t i = 0; i < numsLen; ++i)
	{
		int curLeft = nums[i];
		int curRight = nums[numsLen - i - 1];

		// Left
		// Max
		if (i > 0 && curLeftMax > leftMax)
		{
			leftMax = curLeftMax;
		}

		leftPart[i].max = leftMax;
		if (i == 0)
			leftPart[i].max = 0;
		
		curLeftMax += curLeft;

		alterLeftMax += curLeft;
		if (alterLeftMax > curLeftMax)
		{
			curLeftMax = alterLeftMax;
		}

		if (curLeft < 0)
		{
			alterLeftMax = 0;
		}

		// Min
		if (i > 0 && curLeftMin < leftMin)
		{
			leftMin = curLeftMin;
		}

		leftPart[i].min = leftMin;
		if (i == 0)
			leftPart[i].min = 0;

		curLeftMin += curLeft;

		alterLeftMin += curLeft;
		if (alterLeftMin < curLeftMin)
		{
			curLeftMin = alterLeftMin;
		}

		if (curLeft > 0)
		{
			alterLeftMin = 0;
		}

		// Right
		// Max
		if (i > 0 && curRightMax > rightMax)
		{
			rightMax = curRightMax;
		}

		rightPart[i].max = rightMax;
		if (i == 0)
			rightPart[i].max = 0;

		curRightMax += curRight;

		alterRightMax += curRight;
		if (alterRightMax > curRightMax)
		{
			curRightMax = alterRightMax;
		}

		if (curRight < 0)
		{
			alterRightMax = 0;
		}

		// Min
		if (i > 0 && curRightMin < rightMin)
		{
			rightMin = curRightMin;
		}

		rightPart[i].min = rightMin;
		if (i == 0)
			rightPart[i].min = 0;

		curRightMin += curRight;

		alterRightMin += curRight;
		if (alterRightMin < curRightMin)
		{
			curRightMin = alterRightMin;
		}

		if (curRight > 0)
		{
			alterRightMin = 0;
		}
	}

	// Left
	// Max
	if (curLeftMax > leftMax)
	{
		leftMax = curLeftMax;
	}

	leftPart[numsLen].max = leftMax;

	// Min
	if (curLeftMin < leftMin)
	{
		leftMin = curLeftMin;
	}

	leftPart[numsLen].min = leftMin;

	// Right
	// Max
	if (curRightMax > rightMax)
	{
		rightMax = curRightMax;
	}

	rightPart[numsLen].max = rightMax;

	// Max
	if (curRightMin < rightMin)
	{
		rightMin = curRightMin;
	}

	rightPart[numsLen].min = rightMin;

	for (size_t i = 1; i < numsLen; ++i)
	{
		int leftMax = leftPart[i].max;
		int leftMin = leftPart[i].min;
		int rightMax = rightPart[numsLen - i].max;
		int rightMin = rightPart[numsLen - i].min;

		int abs1 = abs(leftMax - rightMin);
		int abs2 = abs(rightMax - leftMin);

		if (abs1 > maxDiff)
			maxDiff = abs1;
		if (abs2 > maxDiff)
			maxDiff = abs2;
	}

	return maxDiff;
}

